// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, TextField, Button, Typography, Card, CardContent } from '@mui/material';
import api, { setAuthToken } from '../api/api';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState(''); // for UI only
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    // Check if a token exists for the same username
    const storedUser = localStorage.getItem('username');
    const storedToken = localStorage.getItem('token');
    if (storedUser === username && storedToken) {
      setAuthToken(storedToken);
      navigate('/');
      return;
    }
    try {
      // Call the backend to generate a token (backend uses a test user here)
      const response = await api.get('/generate-token');
      const token = response.data.access_token;
      localStorage.setItem('token', token);
      localStorage.setItem('username', username);
      setAuthToken(token);
      navigate('/');
    } catch (error) {
      console.error(error);
      alert('Login failed');
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 4 }}>
      <Card>
        <CardContent>
          <Typography variant="h5" gutterBottom>Login</Typography>
          <form onSubmit={handleLogin}>
            <TextField
              label="Username"
              fullWidth
              margin="normal"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <TextField
              label="Password"
              type="password"
              fullWidth
              margin="normal"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Login
            </Button>
          </form>
        </CardContent>
      </Card>
    </Container>
  );
};

export default Login;
